var res = null
if($this('duration').isNull().not().value()) {
  var stage = 'ContraIndications'
  var list = $this('duration_name')
  var str = []
  var section = $('clsa-dcs.'+stage+':QuestionnaireMetric.section').asSequence()
  var duration = $('clsa-dcs.'+stage+':QuestionnaireMetric.duration').asSequence()
  for(var i = 0; i < list.size(); i++) {
    str.push(
      duration.filter(
        function(v, k) {
          return list.valueAt(i).eq(section.valueAt(k))
        }
      ).asSequence().sum()
    )
  }
  res = newSequence(str, 'integer') 
} else {
  var str = []
  for(var i = 0; i < $this('duration_name').size(); i++) str.push(null)
  res = newSequence(str,'integer')
}
res

