var res = null
var stages = $('clsa-dcs.Participants:Admin.StageInstance.stage').asSequence()
var unique = stages.filter(function(v,i) {
    return i == this.indexOf(v)
  })
var t = newValue(0,'integer')
var group_stages=['ECG','ForearmBoneDensity','DualHipBoneDensity','WholeBodyBoneDensity','LateralBoneDensity']
for(var i = 0; i < unique.size(); i++) {
  var stage = unique.valueAt(i).value()
  var v = null
  if(-1 != group_stages.indexOf(stage)) {
    if('ECG' == stage) {
      v = $('cosmos_dcs.QC_BL_meta:group_duration_ecg').asSequence()
    } else {
      v = $('cosmos_dcs.QC_BL_meta:group_duration_dexa').asSequence()
    }
  } else if('Consent' == stage) {
    var begin = $('clsa-dcs.Consent:timeStart')
    var end = $('clsa-dcs.Consent:timeEnd')
    if(begin.isNull().or(end.isNull()).value()) continue;
    v = end.time().minus(begin.time()).div(1000).asSequence()
  } else if('ConclusionQuestionnaire' == stage) {
    var begin = $('clsa-dcs.ConclusionQuestionnaire:QuestionnaireRun.timeStart')
    var end = $('clsa-dcs.ConclusionQuestionnaire:QuestionnaireRun.timeEnd')
    if(begin.isNull().or(end.isNull()).value()) continue;
    v = end.time().minus(begin.time()).div(1000).asSequence()
  } else {
    v = $group(
      'clsa-dcs.Participants:Admin.StageInstance.stage',
      stage,
      'Admin.StageInstance.duration').asSequence()
  }
  if(null == v) continue
  t = t.plus(v.sum())
}
if(t.gt(0).value()) {
  res = t
}  
res
