var res=null
if($('cosmos_dcs.QC_F1_ecg:contraindicated').not().and(
   $('cosmos_dcs.QC_F1_ecg:file_size').isNull().not()).value()) {
  var ecg=newValue(null,'datetime')
  try {
    ecg=newValue(
    $('clsa-dcs-f1.ECG:RES_MEASUREMENT_DATE').value() + ' ' +
    $('clsa-dcs-f1.ECG:RES_MEASUREMENT_TIME').value(), 'datetime')
  } catch(e) {
  }

  if(ecg.isNull().not().and(ecg.time().gt(0)).value()) {
    var ecgTime=ecg.time()
    var preIndex=-1
    var postIndex=-1
    var pre=newValue(null,'datetime')
    var post=newValue(null,'datetime')
    var minPre=10000000000000000
    var minPost=minPre
    var stages=$('clsa-dcs-f1.Participants:Admin.StageInstance.stage').asSequence().filter(function(v){
      return v.isNull().not().and(v.any('ExtendedHIN','GeneralProxy').not()).value()
    })

    for(var i=0;i<stages.size();i++) {
      var stage=stages.valueAt(i).value()
      if('ECG'==stage) continue
      var isQ = -1==$this('group_questionnaire').indexOf(stage) ? false : true
      var isI = -1==$this('group_instrument').indexOf(stage) ? false : true
      if(!(isQ || isI)) continue
      if(isQ) {
        pre=$('clsa-dcs-f1.'+stage+':QuestionnaireRun.timeEnd')
      } else  {
        pre=$('clsa-dcs-f1.'+stage+':InstrumentRun.timeEnd')
      }
      if(ecg.after(pre).value()) {
        var p=ecgTime.minus(pre.time()).value()
        if(p<minPre) {
          minPre=p
          preIndex=i
        }
      } else {
        if(isQ) {
          post=$('clsa-dcs-f1.'+stage+':QuestionnaireRun.timeStart')
        } else {
          post=$('clsa-dcs-f1.'+stage+':InstrumentRun.timeStart')
        }
        if(ecg.before(post).value()) {
          var p=post.time().minus(ecgTime).value()
          if(p<minPost) {
            minPost=p
            postIndex=i
          }
        }
      }
    }// end for loop
    var valid = false
    if(-1!=preIndex && (postIndex-preIndex)==2) {
      valid = 'ECG' == stages.valueAt(preIndex+1).value()
    }
    else
    {
      var preCandidates = ['BloodPressure','Spirometry','StandingHeight','HipsWaist','Weight']
      for(var i=0;i<preCandidates.length && !valid;i++) {
        preIndex = stages.indexOf(preCandidates[i])
        postIndex = preIndex+1
        if('ECG'==stages.valueAt(postIndex).value()) {
          while('ECG'==stages.valueAt(++postIndex).value()) {}
          valid = 1 < (postIndex-preIndex)
        }
      }
    }
    if(valid) {
      var preStage=stages.valueAt(preIndex).value()
      pre=(-1!=$this('group_questionnaire').indexOf(preStage)) ?
        $('clsa-dcs-f1.'+preStage+':QuestionnaireRun.timeEnd') :
        $('clsa-dcs-f1.'+preStage+':InstrumentRun.timeEnd')
      var postStage=stages.valueAt(postIndex).value()
      post=(-1!=$this('group_questionnaire').indexOf(postStage)) ?
        $('clsa-dcs-f1.'+postStage+':QuestionnaireRun.timeStart') :
        $('clsa-dcs-f1.'+postStage+':InstrumentRun.timeStart')
      if(post.after(pre).value()) {
        res=post.time().minus(pre.time()).div(1000)
      }
    }
  }
}
