var stage = 'Weight'
var action = $group('Admin.Action.stage',stage,'Admin.Action.actionType').asSequence()
var res = $group('Admin.Action.stage',stage,'Admin.Action.eventReason').asSequence().filter(function(v,i){
  return i == action.indexOf('SKIP')
  }).last()
if(res.isNull().not().value()) {
  res = newValue(res.value().slice(14),'text')  
