var res = null
var stages = $('clsa-inhome-f1.Participants:Admin.StageInstance.stage').asSequence()
var unique = stages.filter(function(v,i) {
    return i == this.indexOf(v)
  })
var t = newValue(0,'integer')
for(var i = 0; i < unique.size(); i++  ) {
  var stage = unique.valueAt(i).value()
  var v = null
  if('ConclusionQuestionnaire' == stage) {
    var begin = $('clsa-inhome-f1.ConclusionQuestionnaire:QuestionnaireRun.timeStart')
    var end = $('clsa-inhome-f1.ConclusionQuestionnaire:QuestionnaireRun.timeEnd')
    if(begin.isNull().or(end.isNull()).value()) continue;
    v = end.time().minus(begin.time()).div(1000).asSequence()
  } else {
    v = $group(
      'clsa-inhome-f1.Participants:Admin.StageInstance.stage',
      stage,
      'Admin.StageInstance.duration').asSequence()
  }
  if(null == v) continue
  t = t.plus(v.sum())
}
if(t.gt(0).value()) {
  res = t
}  
