var list = ['LateralBoneDensity','WholeBodyBoneDensity','DualHipBoneDensity','ForearmBoneDensity','SpineBoneDensity']
var res = null
for(var i=0; i<list.length; i++) {
  var str = $('clsa-dcs-images-f2.'+list[i]+':InstrumentRun.user').whenNull(
    $group('Admin.Action.stage',list[i],'Admin.Action.user').asSequence().last()).lowerCase()
  if(str.isNull().not().value()) {
    var v = str.value()
    if(0<v.length) {
      res = newValue(v)
      break
    }  
  }  
}
res

var prefix = 'cosmos_dcs.QC_F2_dexa_'
var list = ['hip','whole_body','lateral','forearm','spine']
var str = []
for(var i=0; i<list.length; i++) {
  var scan = list[i]  
  if('hip'==scan) {
    str.push($(prefix+scan+':file_size').valueAt(0))
    str.push($(prefix+scan+':file_size').valueAt(1))
  } else {
    str.push($(prefix+scan+':file_size'))
  }
}
newSequence(str,'integer')

var res = null
if($this('contraindicated').not().value()) {
  var q = 0
  q += ($('cosmos_dcs.QC_F2_dexa_lateral:contraindicated').value() ? 0 : 1)
  q += ($('cosmos_dcs.QC_F2_dexa_spine:contraindicated').value() ? 0 : 1)
  q += ($('cosmos_dcs.QC_F2_dexa_whole_body:contraindicated').value() ? 0 : 1)
  q += ($('cosmos_dcs.QC_F2_dexa_forearm:contraindicated').value() ? 0 : 1)
  //
  // during F2 only 1 hip is acquired
  //
  q += ($('cosmos_dcs.QC_F2_dexa_hip:contraindicated').value() ? 0 : 1)
  res = $this('file_size').trimmer().size().div(q).multiply(100)
}
res
