  })
var t = newValue(0,'integer')
var group_stages=['ECG','ForearmBoneDensity','DualHipBoneDensity','WholeBodyBoneDensity','LateralBoneDensity','SpineBoneDensity']
var alternate=['Consent','ExtendedHIN','GeneralProxy']
for(var i = 0; i < unique.size(); i++) {
  var stage = unique.valueAt(i).value()
  var v = null
  if(-1 != group_stages.indexOf(stage)) {
    if('ECG' == stage) {
      v = $('cosmos_dcs.QC_F2_meta:group_duration_ecg').asSequence()
    } else {
      v = $('cosmos_dcs.QC_F2_meta:group_duration_dexa').asSequence()
    }
  } else if(-1 != alternate.indexOf(stage)) {
    var begin = $('clsa-dcs-f2.' + stage + ':timeStart')
    var end = $('clsa-dcs-f2.' + stage + ':timeEnd')
    if(begin.isNull().or(end.isNull()).value()) continue;
    v = end.time().minus(begin.time()).div(1000).asSequence()
  } else {
    v = $group(
      'clsa-dcs-f2.Participants:Admin.StageInstance.stage',
      stage,
      'Admin.StageInstance.duration').asSequence().filter(function(v) {
        return v.ge(0)
      })
  }    
  if(null == v) continue
  t = t.plus(v.sum())
}

