// this script compounds repeated comments by using lowercase comment strings as array keys
//
var stage = 'BodyCompositionWeight'
var c_seq = $group('Admin.Action.stage',stage,'Admin.Action.comment').asSequence()
var e_seq = $group('Admin.Action.stage',stage,'Admin.Action.eventReason').asSequence()
var res = null
if(c_seq.size().eq(e_seq.size()).value())
{
  var c_str = []  // string array of comments
  var e_str = []  // string array of event reasons

  for(var i = 0; i < c_seq.size(); i++) {
    var c = c_seq.valueAt(i).value()
    if(null == c) continue
    c = c.replace(/[^a-zA-Z0-9 àâäèéêëîïôœùûüÿçÀÂÄÈÉÊËÎÏÔŒÙÛÜŸÇ]/g,"")
    var e = e_seq.valueAt(i).value()
    if(0 < c.length) {
      c_str.push(c)
      if(null != e && 0 < e.length) {
        e = e.slice(14)
      } else {
        e = 'SeeComment'
      }
      e_str.push(e)
    }
  }

  if(0 < c_str.length) {
    var arr = []  // array of event reasons
    var key_orig = []  // array of lower cased comments as unique keys
    for(var i = 0; i < c_str.length; i++) {
      var key = c_str[i]
      var key_lower = key.toLowerCase()
      // retain the first encountered instance of the comment
      //
      if(undefined === key_orig[key_lower])
        key_orig[key_lower] = key

      if(undefined === arr[e_str[i]])
        arr[e_str[i]] = []

      if(-1==arr[e_str[i]].indexOf(key_orig[key_lower]))
        arr[e_str[i]].push(key_orig[key_lower])
    }

    // loop over the final array of event reasons and their sets of comments
    //
    var str = '{'
    for(var key in arr) {
      str += '"' + key + '":['
      var t = []
      for(var i = 0; i < arr[key].length; i++) {
        t.push('"' + arr[key][i] + '"')
      }
      str += t.join(',') + '],'
    }

    res = newValue(str.substring(0,str.length-1)+'}','text')
  }
}
res

var stage = 'BodyCompositionWeight'
var p = $('clsa-dcs-f2.' + stage + ':QuestionnaireMetric.missingCount').asSequence().sum()
var q = $('clsa-dcs-f2.' + stage + ':QuestionnaireMetric.questionCount').asSequence().sum()
q.minus(p).multiply(100).div(q)

var stage = 'BodyCompositionWeight'
var res = null
var post = $('clsa-dcs-f2.'+stage+':QuestionnaireRun.timeEnd')
var pre = $('clsa-dcs-f2.'+stage+':QuestionnaireRun.timeStart')
if(pre.before(post).value()) {
    res = post.time().minus(pre.time()).div(1000)
}
if(null === res) {
  res = $group(
    'clsa-dcs-f2.Participants:Admin.StageInstance.stage',
    stage,
    'Admin.StageInstance.duration').asSequence().sum()
}
if(res.eq(0).value()) res = null
res

var stage = 'BodyCompositionWeight'
var action = $group('clsa-dcs-f2.Participants:Admin.Action.stage',stage,'Admin.Action.actionType').asSequence()
var res = $group('clsa-dcs-f2.Participants:Admin.Action.stage',stage,'Admin.Action.eventReason').asSequence().filter(function(v,i){
  return i == action.indexOf('SKIP')
  }).last()
if(res.isNull().not().value()) {
  res = newValue(res.value().slice(14),'text')  
}
res

var res = null
if($this('duration').isNull().not().value()) {
  var stage = 'BodyCompositionWeight'
  var str = []
  var section = $('clsa-dcs-f2.'+stage+':QuestionnaireMetric.section').asSequence()
  var list = section.trimmer().filter(function(v,i){return i==this.indexOf(v)})
  var duration = $('clsa-dcs-f2.'+stage+':QuestionnaireMetric.duration').asSequence()  
  for(var i = 0; i < list.size(); i++) {
    var v =
      duration.filter(
        function(v, k) {
          return section.valueAt(k).eq(list.valueAt(i)).value()
        }
      ).asSequence().filter(function(v) {
        return v.gt(0)
      }).sum()
    if(v.isNull().value()) continue
    str.push('"' + list.valueAt(i).value() + '":' + v.value())
  }
  if(0<str.length) {
    res = newValue('{'+str.join(',')+'}','text')
  }  
}
res
