var prj = 'cosmos_inhome.QC_F2_'

var view_list = {
  'interview':
    ['barcode','start_date','site','duration','stage_duration'],
  'inhome_1':
    ['technician','contraindicated','comment','duration','missing','qnaire_complete','skip','module_duration'],
  'inhome_2':
    ['technician','contraindicated','comment','duration','missing','qnaire_complete','skip','module_duration'],
  'inhome_3':
    ['technician','contraindicated','comment','duration','missing','qnaire_complete','skip','module_duration'],
  'inhome_4':
    ['technician','contraindicated','comment','duration','missing','qnaire_complete','skip','module_duration'],
  'inhome_cognition_recording':
    ['technician','contraindicated','comment','duration','missing','qnaire_complete','skip','file_complete','file_size']  
}

var inter_str = null
var stage_str = []
for( var key in view_list ) {  
  var var_str = prj + key + ':'
  var var_arr = view_list[key]  
  key_str = []
  for(var i=0;i<var_arr.length;i++) {
    var var_i = var_arr[i]
    var v = $(var_str + var_i)
    if(v.isNull().value()) continue;    
    if('module_duration'==var_i || 'file_size'==var_i) {  
      key_str.push('"' + var_i + '":' + v.value() + '')
    } else {
      if(v.type().eq('text').and('comment'!=var_i).value()) {
        key_str.push('"' + var_i + '":"' + v.value() + '"')
      } else {
        key_str.push('"' + var_i + '":' + v.value() + '')
      }  
    }
  }
  if('interview'==key) {
    inter_str = '{' + key_str.join(',') + ',"stages":{'
  } else {
    stage_str.push( '"' + key + '":{' + key_str.join(',') + '}')
  }    
}

newValue( inter_str + stage_str.join(',') +'}}','text')
