var res = null

if($('clsa-dcs-f3.ECG:RES_MEASUREMENT_DATE').isNull().not().and(
   $('clsa-dcs-f3.ECG:RES_MEASUREMENT_TIME').isNull().not()).value()) {
  var ecg = newValue(
    $('clsa-dcs-f3.ECG:RES_MEASUREMENT_DATE').value() + ' ' +
    $('clsa-dcs-f3.ECG:RES_MEASUREMENT_TIME').value(), 'datetime')

  var ecgTime = ecg.time()
  var preIndex = -1
  var postIndex = -1    
  var pre = newValue(null, 'datetime')
  var post = newValue(null, 'datetime')
  var minPre = 10000000000000000
  var minPost = minPre  
  var stages = $('clsa-dcs-f3.Participants:Admin.StageInstance.stage').asSequence().filter(function(v) {
    return v.matches(/ECG/).not().value()
  }) 
  var times = {'pre':null,'post':null}

  for(var i = 0; i < stages.size(); i++) {
    var stage = stages.valueAt(i).value()    

    if(-1 != $this('group_questionnaire').indexOf(stage)) {
      pre = $('clsa-dcs-f3.'+stage+':QuestionnaireRun.timeEnd')      
    } else if(-1 != $this('group_instrument').indexOf(stage)) {
      pre = $('clsa-dcs-f3.'+stage+':InstrumentRun.timeEnd')      
    } else if(-1 != $this('group_other').indexOf(stage)) {
      pre = $('clsa-dcs-f3.'+stage+':timeEnd') 
    } else {
      continue
    }
    if(ecg.after(pre).value()) {
      var p = ecgTime.minus(pre.time()).value()
      if(p < minPre) {
        minPre = p
        preIndex = i
        times['pre'] = pre.time()
      }        
    } else {
      if(-1 != $this('group_questionnaire').indexOf(stage)) {        
        post = $('clsa-dcs-f3.'+stage+':QuestionnaireRun.timeStart')
      } else if(-1 != $this('group_instrument').indexOf(stage)) {        
        post = $('clsa-dcs-f3.'+stage+':InstrumentRun.timeStart')
      } else if(-1 != $this('group_other').indexOf(stage)) {        
        post = $('clsa-dcs-f3.'+stage+':timeStart')
      } else {
        continue
      }     
      if (ecg.before(post).value()) {
        var p = post.time().minus(ecgTime).value()
        if(p < minPost) {
          minPost = p
          postIndex = i
          times['post'] = post.time()
        }
      }
    }
  }
  if(1<(postIndex-preIndex)) {
    res = times['post'].minus(times['pre']).div(1000)    
  }
}

if(null == res) {
  var post = $('clsa-dcs-f3.ECG:InstrumentRun.timeEnd')
  var pre = $('clsa-dcs-f3.ECG:InstrumentRun.timeStart')
  if(pre.before(post).value()) {
    res = post.time().minus(pre.time()).div(1000)
  }
  if(null == res) {
    res = $group(
      'clsa-dcs-f3.Participants:Admin.StageInstance.stage',
      'ECG',
      'Admin.StageInstance.duration').asSequence().sum()
  }
  if(res.eq(0).value()) res = null
}

res

var list = ['LateralBoneDensity','ForearmBoneDensity','WholeBodyBoneDensity','DualHipBoneDensity','SpineBoneDensity']
var stages = $('clsa-dcs-f3.Participants:Admin.StageInstance.stage').asSequence()
var dexa = stages.filter(function(v) {
    return -1!=list.indexOf(v.value())
  })
var res = null

if(dexa.empty().not().value()) {

  list=list.concat(['QuestionnaireBoneDensity','Frax','EventPMT'])
  var seq = stages.filter(function(v) {
    return -1!=list.indexOf(v.value())
  }).join('').value()

  var stageAfter = seq.endsWith('FraxEventPMT') ? 'Frax' : 'EventPMT'
  // bracket the actual bone density acquisitions
  // 
  var pre = $('clsa-dcs-f3.QuestionnaireBoneDensity:QuestionnaireRun.timeEnd')  
  var post = -1 != $this('group_questionnaire').indexOf(stageAfter) ?
    $('clsa-dcs-f3.'+stageAfter+':QuestionnaireRun.timeStart') :
    $('clsa-dcs-f3.'+stageAfter+':InstrumentRun.timeStart')

  if(pre.before(post).value()) {    
    res = post.time().minus(pre.time()).div(1000)
  }  
}

if(null == res) {
  res = newValue(0,'integer')
  list = ['LateralBoneDensity','ForearmBoneDensity','WholeBodyBoneDensity','DualHipBoneDensity','SpineBoneDensity']
  list.forEach(function(stage) {
    var pre = $('clsa-dcs-f3.'+stage+':InstrumentRun.timeEnd')
    var post = $('clsa-dcs-f3.'+stage+':InstrumentRun.timeStart')  
    if(pre.before(post).value()) {    
      res = res.plus(post.time().minus(pre.time()).div(1000))
    }    
  })
  if(res.eq(0).value()) res = null
}

res

var res = null
var u = $group('clsa-dcs-f3.Participants:Admin.StageInstance.stage','Blood','Admin.StageInstance.lastState').asSequence().last()
var v = $group('clsa-dcs-f3.Participants:Admin.StageInstance.stage','Blood2','Admin.StageInstance.lastState').asSequence().last()
var p = $('clsa-dcs-f3.Participants:Admin.StageInstance.stage').any('Blood').and(u.isNull().not()).value()
var q = $('clsa-dcs-f3.Participants:Admin.StageInstance.stage').any('Blood2').and(v.isNull().not()).value()
if(p && !q) {
  res = newValue( 'Blood', 'text') 
} else if(q && !p) {
  res = newValue( 'Blood2', 'text') 
} else if(p && q) {
  if(v.any('NotApplicable').or(u.any('Completed')).value()) {
    res = newValue('Blood', 'text')
  } else { 
    res = newValue('Blood2', 'text')
  }
}
