// this script compounds repeated comments by using lowercase comment strings as array keys
//
var stage = 'InHome_2'
var c_seq = $group('Admin.Action.stage',stage,'Admin.Action.comment').asSequence()
var e_seq = $group('Admin.Action.stage',stage,'Admin.Action.eventReason').asSequence()
var res = null
if(c_seq.size().eq(e_seq.size()).value())
{
  var c_str = []  // string array of comments
  var e_str = []  // string array of event reasons

  for(var i = 0; i < c_seq.size(); i++) {
    var c = c_seq.valueAt(i).value()
    if(null == c) continue
    c = c.replace(/[^a-zA-Z0-9 àâäèéêëîïôœùûüÿçÀÂÄÈÉÊËÎÏÔŒÙÛÜŸÇ]/g,"")
    var e = e_seq.valueAt(i).value()
    if(0 < c.length) {
      c_str.push(c)
      if(null != e && 0 < e.length) {
        e = e.slice(14)
      } else {
        e = 'SeeComment'
      }
      e_str.push(e)
    }
  }

  if(0 < c_str.length) {
    var arr = []  // array of event reasons
    var key_orig = []  // array of lower cased comments as unique keys
    for(var i = 0; i < c_str.length; i++) {
      var key = c_str[i]
      var key_lower = key.toLowerCase()
      // retain the first encountered instance of the comment
      //
      if(undefined === key_orig[key_lower])
        key_orig[key_lower] = key

      if(undefined === arr[e_str[i]])
        arr[e_str[i]] = []

      if(-1==arr[e_str[i]].indexOf(key_orig[key_lower]))
        arr[e_str[i]].push(key_orig[key_lower])
    }

    // loop over the final array of event reasons and their sets of comments
    //
    var str = '{'
    for(var key in arr) {
      str += '"' + key + '":['
      var t = []
      for(var i = 0; i < arr[key].length; i++) {
        t.push('"' + arr[key][i] + '"')
      }
      str += t.join(',') + '],'
    }

    res = newValue(str.substring(0,str.length-1)+'}','text')
  }
}
res

var res = null
var q = $('clsa-inhome-f3.InHome_2:QuestionnaireMetric.questionCount').asSequence()
var p = $('clsa-inhome-f3.InHome_2:QuestionnaireMetric.missingCount').asSequence()
var s = $('clsa-inhome-f3.InHome_2:QuestionnaireMetric.section').asSequence()
if(s.size().eq(q.size()).and(p.size().eq(q.size())).value()) {
  var i = s.lastIndexOf('COG')
  var j = i-1
  while(j>=0 && s.valueAt(j).matches(/COG/).value()) { j-- }
  var qsum = q.subset(j+1,i+1).sum()
  var psum = p.subset(j+1,i+1).sum()
  res = qsum.minus(psum).multiply(100).div(qsum)
}
res

