var eye=$('clsa-dcs.Tonometer:Measure.TON_EYE').lowerCase().map({
  'l':'L',
  'r':'R',
  'right':'R',
  'left':'L',
  'left eye':'L',
  'right eye':'R',
  'righr':'R',
  'rigth':'R',
  'rught':'R',
  'lrft':'L',
  '0':'L',
  '1':'R',
  'll':'L',
  'rr':'R',
  'le':'L',
  'leeft':'L',
  'gauche':'L',
  'droit':'R',
  'droite':'R'},null)

// bare minimum useful data is IOPcc: Corneal Compensated Intraocular Pressure
var iopcc=$('clsa-dcs.Tonometer:Measure.TON_IOPCC').asSequence() 

// if there is raw applanation and pressure data there will be a quality index
// for each set of measurements
var qual=$('clsa-dcs.Tonometer:Measure.TON_QUALITYINDEX').asSequence()
var appl=$('clsa-dcs.Tonometer:Measure.TON_PRESSURE').asSequence()
var pres=$('clsa-dcs.Tonometer:Measure.TON_APPLANATION').asSequence()

var has_iopcc=iopcc.size().eq(eye.size()).value()
var has_qual=qual.size().eq(eye.size()).value()
var has_appl=appl.size().eq(eye.size()).value()
var has_pres=pres.size().eq(eye.size()).value()
var res=null
if(eye.isNull().not().value()) {
  var side={L:{left_iopcc:'0',left_qi:'-1',left_appl:'0',left_pres:'0'},
            R:{right_iopcc:'0',right_qi:'-1',right_appl:'0',right_pres:'0'}}
  for(var i=0;i<eye.size();i++) {
    var e=eye.valueAt(i).value()
    if(has_iopcc) {
      var q=newValue(iopcc.valueAt(i).value(),'text')
      if(q.matches(/^(\d*\.)?\d+$/)) {
        if('L'==e) {
          side[e].left_iopcc='1'
        } else if('R'==e) {
          side[e].right_iopcc='1'
        }
      }
    }
    if(has_qual) {
      var q=newValue(qual.valueAt(i).round(2).value(),'text')
      if(q.matches(/^(\d*\.)?\d+$/)) {
        if('L'==e) {
          side[e].left_qi=q.value()
        } else if('R'==e) {
          side[e].right_qi=q.value()
        }
      }
    }
    if(has_appl) {
      var q=appl.valueAt(i).value().split(',').length
      if('L'==e) {
        side[e].left_appl=q
      } else if('R'==e) {
        side[e].right_appl=q
      }      
    }        
    if(has_pres) {
      var q=pres.valueAt(i).value().split(',').length
      if('L'==e) {
        side[e].left_pres=q
      } else if('R'==e) {
        side[e].right_pres=q
      }
    }
  }
  res=newValue(
    JSON.stringify(side.L).replace(/"/g,'').replace(/}/g,',') +
    JSON.stringify(side.R).replace(/"/g,'').replace(/{/g,''), 'text')
}
res

