attributes.attribute:
var res=null
if($('Quality_Control.QC_BL_IH:stage_contraindicated_inhome_1').eq(0).and(
   $('Quality_Control.QC_BL_IH:stage_skip_inhome_1').isNull()).value()) {
  var post = $('clsa-inhome.InHome_1:QuestionnaireRun.timeEnd')
  var pre = $('clsa-inhome.InHome_1:QuestionnaireRun.timeStart')
  if(pre.before(post).value()) {
    res=post.time().minus(pre.time()).div(60000)
  }
  if(null===res) {
    res=$group(
      'clsa-inhome.Participants:Admin.StageInstance.stage',
      'InHome_1',
      'Admin.StageInstance.duration').asSequence().last().div(60)
  }  
}
if(null!==res && res.isNull().not().value()) {
  var list=['ALC','ED','GEN','NUT','OWN','SDC','SMK','VET']
  var str=['stage:'+res.round(3)]
  var section=$('clsa-inhome.InHome_1:QuestionnaireMetric.section').asSequence()
  var duration=$('clsa-inhome.InHome_1:QuestionnaireMetric.duration').asSequence()
  var u=0
  for(var i=0;i<list.length;i++) {
    var m=list[i]
    var t=duration.filter(function(v,k){
      return section.valueAt(k).value()==m
    }).asSequence().whenNull(0).sum().div(60)
    u+=t
    str.push(m+':'+ Math.round(t*1000)/1000)
  }
  str.push('module:'+Math.round(u*1000)/1000)
  res=newValue('{'+str.join(',')+'}','text')  
}
res

