var res=null
if($('Quality_Control.QC_BL_IH:stage_contraindicated_inhome_conclusion_questionnaire').eq(0).and(
   $('Quality_Control.QC_BL_IH:stage_skip_inhome_conclusion_questionnaire').isNull()).value()) {
  var post = $('clsa-inhome.ConclusionQuestionnaire:QuestionnaireRun.timeEnd')
  var pre = $('clsa-inhome.ConclusionQuestionnaire:QuestionnaireRun.timeStart')
  if(pre.before(post).value()) {
    res=newValue('{stage:'+post.time().minus(pre.time()).div(60000).round(3)+'}','text')
  } else {
    var v=$group(
      'clsa-inhome.Participants:Admin.StageInstance.stage',
      'ConclusionQuestionnaire',
      'Admin.StageInstance.duration').asSequence().last()
    if(v.isNull().not().value()) {
      res=newValue('{stage:'+v.div(60).round(3)+'}','text')
    }      
  }
}
