if($('Quality_Control.QC_F1:stage_contraindicated_carotid_intima').eq(0).and(
  $this('group_duration_ecg').isNull().not()).value()) {
  var stages=$('clsa-dcs-f1.Participants:Admin.StageInstance.stage').asSequence()
  var preIndex=$this('group_duration_ecg').first().value()
  var postIndex=$this('group_duration_ecg').last().value()
  var cimtStages=stages.filter(function(v,i){
    return (preIndex<=i && i<=postIndex)
  })
  var ecgIndex=cimtStages.indexOf('ECG').value()
  var cimtIndex=cimtStages.indexOf('CarotidIntima').value()
  if(-1!=ecgIndex && -1!=cimtIndex) {
    var pre=newValue(null,'datetime')
    var post=newValue(null,'datetime')
    // get the stage immediately before cimt
    cimtIndex=0
    for(var i=postIndex;i>0;i--) {
      var stage=stages.valueAt(i).value()
      if('CarotidIntima'==stage) {
        cimtIndex=i
        break
      }
    }
    for(var i=cimtIndex-1;i>0;i--) {
      var stage=stages.valueAt(i).value()
      var isQ=-1!=$this('group_questionnaire').indexOf(stage)
      var isI=-1!=$this('group_instrument').indexOf(stage)
      if(!(isQ || isI)) continue
      pre=isQ ?
        $('clsa-dcs-f1.'+stage+':QuestionnaireRun.timeEnd') :
        $('clsa-dcs-f1.'+stage+':InstrumentRun.timeEnd')
      break
    }
    // get the stage immediately after cimt
    for(var i=cimtIndex+1;i<postIndex+5;i--) {
      var stage=stages.valueAt(i).value()
      var isQ=-1!=$this('group_questionnaire').indexOf(stage)
      var isI=-1!=$this('group_instrument').indexOf(stage)
      if(!(isQ || isI)) continue
      post=isQ ?
        $('clsa-dcs-f1.'+stage+':QuestionnaireRun.timeStart') :
        $('clsa-dcs-f1.'+stage+':InstrumentRun.timeStart')
      break
    }

    if(post.after(pre).value()) {
      res=newValue('{stage:'+post.time().minus(pre.time()).div(60000).round(3)+'}','text')
    }
  }
}
