var res=null
if($('Quality_Control.QC_F1:stage_contraindicated_general_health').eq(0).and(
   $('Quality_Control.QC_F1:stage_skip_general_health').isNull()).value()) {
  var post = $('clsa-dcs-f1.GenHealth:QuestionnaireRun.timeEnd')
  var pre = $('clsa-dcs-f1.GenHealth:QuestionnaireRun.timeStart')
  if(pre.before(post).value()) {
    res=post.time().minus(pre.time()).div(60000)
  }
  if(null===res) {
    res=$group(
      'clsa-dcs-f1.Participants:Admin.StageInstance.stage',
      'GenHealth',
      'Admin.StageInstance.duration').asSequence().last().div(60)
  }
}
if(null!==res && res.isNull().not().value()) {
  var list=['HRG','HUP','NUR','VIS']

  var str=[('stage:'+Math.round(res*1000)/1000)]
  var section=$('clsa-dcs-f1.GenHealth:QuestionnaireMetric.section').asSequence()
  var duration=$('clsa-dcs-f1.GenHealth:QuestionnaireMetric.duration').asSequence()
  var u=0
  for(var i=0;i<list.length;i++) {
    var m=list[i]
    var t=duration.filter(function(v,k){
      return section.valueAt(k).value()==m
    }).asSequence().whenNull(0).sum().div(60)
    u+=t
    str.push(m+':'+ Math.round(t*1000)/1000)
  }
  str.push('module:'+Math.round(u*1000)/1000)
  res=newValue('{'+str.join(',')+'}','text')
}
res

