attributes.attribute:
// this script compounds repeated comments by using lowercase comment strings as array keys
// and further compounds events within actions
// 
var v1=$group('Admin.Action.stage','ConclusionQuestionnaire','Admin.Action.comment').asSequence()
var v2=$group('Admin.Action.stage','ConclusionQuestionnaire','Admin.Action.actionType').asSequence()
var v3=$group('Admin.Action.stage','ConclusionQuestionnaire','Admin.Action.eventReason').asSequence()
var res=null
var c_str=[]  // string array of comments
var e_str=[]  // string array of events
var a_str=[]  // string array of actions
for(var i=0; i<v1.size(); i++) {  
  if(v1.valueAt(i).isNull().or(v2.valueAt(i).isNull(),v2.valueAt(i).matches(/EXECUTE/)).value()) continue
  c_str.push(v1.valueAt(i).value())
  a_str.push(v2.valueAt(i).value())
  e_str.push((v3.valueAt(i).isNull().value()?'SeeComment':v3.valueAt(i).value().slice(14)))
}
if(0<c_str.length) {
  var arr=[]
  var key_orig=[]
  for(var i=0;i<c_str.length;i++) {
    var key=c_str[i].replace(/[^a-zA-Z0-9 àâäèéêëîïôœùûüÿçÀÂÄÈÉÊËÎÏÔŒÙÛÜŸÇ]/g,"")
    var key_lower = key.toLowerCase()    
    // retain the first encountered instance of the comment
    if(undefined===key_orig[key_lower])
      key_orig[key_lower]=key
    if(0==key_orig[key_lower].length) continue
    // combine the action and the event
    var str=a_str[i]+':'+e_str[i]
    if(undefined===arr[key_lower])
      arr[key_lower]=[]
    if(-1===arr[key_lower].indexOf(str))
      arr[key_lower].push(str)
  }

  var str=[]
  for(var key in arr) {
    // compound repeated events 
    var subarr=[]
    for(var i=0;i<arr[key].length;i++) {
      var t=arr[key][i].split(':')
      var t_key=t[1]
      if(undefined===subarr[t_key])
        subarr[t_key]=[]
      if(-1===subarr[t_key].indexOf(t[0]))  
        subarr[t_key].push(t[0])
    }
    var substr=[]
    for(var t_key in subarr) {
      var t_str='SeeComment'==t_key?']':']'+t_key
      if(1<subarr[t_key].length) {
        substr.push('['+subarr[t_key].join('/')+t_str);
      } else {
        substr.push('['+subarr[t_key][0]+t_str);
      }
    }
    str.push('['+((1<substr.length)?substr.join('/'):substr[0])+']:'+key_orig[key])
  }

  if(1<str.length) {
    res=newValue(str.join(','),'text')
  } else if(1==str.length) {
    res=newValue(str[0],'text')
  }
}
res

